    private void registrarBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_registrarBtnActionPerformed
        OrdemServico os = new OrdemServico();

        //VARIÁVEIS PARA OS SETS
        String nome = nomeTextField.getText();
        String telefone = telefoneTextField.getText();
        String preco = precoTextField.getText();

        //CAMPOS DE TEXTO
        if (nome.isBlank()) {
            JOptionPane.showMessageDialog(null, "Nome do cliente vazio! Favor inserir para prosseguir!");
        } else {
            os.setNomeCliente(nome);
            if (telefone.isBlank()) {
                JOptionPane.showMessageDialog(null, "Telefone vazio! Favor inserir para prosseguir!");
            } else {
                os.setTelefone(telefone);
                if (preco.isBlank()) {
                    JOptionPane.showMessageDialog(null, "Preço vazio! Favor inserir para prosseguir!");
                } else {
                    if (!OrdemServico.validarPreco(preco)) {
                    } else {
                        preco = preco.replace(",", ".");
                        os.setPreco(Double.parseDouble(preco));

                        DateTimeFormatter form = DateTimeFormatter.ofPattern("dd/MM/yyyy HH:mm:ss");
                        os.setDataRecepcao(LocalDateTime.now().format(form));
                        if (!simRadioBtn.isSelected() & !naoRadioBtn.isSelected()) {
                            JOptionPane.showMessageDialog(null, "Favor definir se já foi pago!");
                        } else {
                            if (simRadioBtn.isSelected()) {
                                if (!dinheiroRadioBtn.isSelected() & !debitoRadioBtn.isSelected() & !creditoRadioBtn.isSelected() & !pixRadioBtn.isSelected()) {
                                    JOptionPane.showMessageDialog(null, "Nenhuma forma de pagamento foi definida! Defina uma antes de prosseguir!");
                                } else {
                                    os.setFormaPagamento(formaPagarGp.getSelection().getActionCommand());
                                }
                            }
                            os.setJaPago(jaPagoGp.getSelection().getActionCommand());

                            if (servicoTextArea.getText().isBlank()) {
                                JOptionPane.showMessageDialog(null, "Serviço está vazio! Descreva o que está sendo vendido!");
                            } else {
                                os.setServico(servicoTextArea.getText());

                                if (OsDAO.cadastrarOS(os)) {
                                    JOptionPane.showMessageDialog(null, "O.S registrada com sucesso!");
                                    nomeTextField.setText("");
                                    telefoneTextField.setText("");
                                    servicoTextArea.setText("");
                                    precoTextField.setText("");
                                    formaPagarGp.clearSelection();
                                    jaPagoGp.clearSelection();
                                }
                            }
                        }
                    }
                }
            }
        }
    }//GEN-LAST:event_registrarBtnActionPerformed

