    private void simRadioBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_simRadioBtnActionPerformed
        simRadioBtn.setActionCommand("Sim");

        if (simRadioBtn.isSelected()) {
            formaPagPanel.setEnabled(true);
            dinheiroRadioBtn.setEnabled(true);
            debitoRadioBtn.setEnabled(true);
            creditoRadioBtn.setEnabled(true);
            pixRadioBtn.setEnabled(true);
            formaPagamentoLabel.setEnabled(true);
        }
    }//GEN-LAST:event_simRadioBtnActionPerformed

    private void naoRadioBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_naoRadioBtnActionPerformed
        naoRadioBtn.setActionCommand("Não");

        if (naoRadioBtn.isSelected()) {
            formaPagPanel.setEnabled(false);
            dinheiroRadioBtn.setEnabled(false);
            debitoRadioBtn.setEnabled(false);
            creditoRadioBtn.setEnabled(false);
            pixRadioBtn.setEnabled(false);
            formaPagamentoLabel.setEnabled(false);
            formaPagarGp.clearSelection();
        }
    }//GEN-LAST:event_naoRadioBtnActionPerformed

